mapper.insert:
INSERT INTO notice (
    notice_sort, notice_id, title, content, post_date
) VALUES (
    #{notice_sort}, NOTICE_SEQ.NEXTVAL, #{title}, #{content}, sysdate
)

mapper.update:
UPDATE notice
SET title = #{title}, content = #{content}, notice_sort = #{notice_sort}
WHERE notice_id = #{notice_id}
